rule "geoip-lookup"
when
  has_field("clientip")
then
  let result = lookup("geoip", to_string($message.clientip));
  let location = result.location;
  let lat = to_string(location.latitude);
  let lng = to_string(location.longitude);
  let coordinates = concat(lat, concat(",", lng));
  set_field("coordinates", coordinates);

  let city = to_string(result.city.names.en);
  let country = to_string(result.country.names.en);
  let continent = to_string(result.continent.names.en);
  set_field("city", city);
  set_field("country", country);
  set_field("continent", continent);
end
